import React from 'react'
import {FCLike} from "react-icons/fc";
import { toast } from 'react-toastify';

const Card = (props) => {
     let course = props.course;
     let LikedCourses = props.LikedCourses;
     let setLikedCourses = props.setLikedCourses;
     function clickHandler() {
       if(LikedCourses.include(course.id)){
          setLikedCourses( (prev) => prev.filter( (cid !== course.id) ) );
       }
       else{
          if(LikedCourses.leght === 0){
            setLikedCourses([course.id]);
          }
          else{
            setLikedCourses((prev) => [...prev, course.id]);
          }
          toast.success("Liked Successfully");
       }
     }
    return(
      <div className="w-[300px] bg-bgDark bg-opacity-50 rounded-md overflow-hidden">
        <div className="relative">
            <img src={course.image.url}></img>

            <div className="w-[30px] h-[30px] bg-white rounded-full absolute right-2 bottom-3 grid place-items-center">
              <button onClick={clickHandler}>
                <FcLike fontSize="1.75rem"/>
               </button>
            </div>
        </div>

        <div className="p-4">
            <p className="text-white font-semibold text-lg leading-6">{course.title}</p>
            <p className="mt-2 text-white">{course.description}</p>
        </div>
      </div>
    )
  }
export default Card;
